mapper.select:
/** com.playground.common.dao.MemberDao.count  */
SELECT 	COUNT(1) CNT
	FROM T_USER 
 WHERE memberid = #{memberid}

mapper.update:
/** com.playground.common.dao.MemberDao.update */
UPDATE T_USER
SET UPT_DT = NOW()
, UPT_ID = #{uptId}

, member_name = #{memberName}
, mobile1 = #{mobile1}
, mobile2 = #{mobile2}
, email = #{email}
, email_domain = #{emailDomain}
, email_auth = #{emailAuth}
, passwd = #{passwd}
, member_type = #{memberType}
, last_login = NOW()
, refund_banknm = #{refundBanknm}
, refund_account = #{refundAccount}
, refund_name = #{refundName}

WHERE memberid = #{memberid}

/** com.playground.common.dao.MemberDao.authMember */
UPDATE T_USER
SET email_auth = "true"

WHERE memberid = #{memberid}
